/*	 	Name:					Date Created:		Date Modified:		Program #:			10	Description:			Bonus:				*/public class Program10 {	public static void main(String args[]) {		EasyReader console = new EasyReader();		int pcNum = (int) (Math.random() * 100) + 1;		int userNum = 0;		int i = 0;		System.out.print("The computer is thinking of a number between 1-100. Guess the number...");		do {			System.out.print(" \nGuess = ");			userNum = console.readInt();			if (userNum > pcNum) {				System.out.println("\n" + userNum + " is too high! Guess again. ");				i = i + 1;			} else if (userNum < pcNum) {				System.out.println("\n" + userNum + " is too low! Guess again. ");				i = i + 1;			} else {				i = i + 1;				System.out.println("\n" + "You got it! " + pcNum + " is the right answer." + "\n\nIt took you " + i + " guesses.");			}		} while (userNum != pcNum);	}}