/*	Name:					Date Created	Date 	Program #:			9	Description:		This program will tell the user whether a number is prime or composite.	Bonus:				*/public class Program9 {	public static void main(String args[]) {		EasyReader console = new EasyReader();		boolean again = true;		do {			System.out.println("Num = ");			int number = console.readInt();			for (int i = 2; i <= number / 2; i++) {				if (number % i == 0) {					System.out.println("\n" + number + " is a composite number");				}			}			System.out.println("\n" + number + " is a prime number");			System.out.print("\n" + "Would you like to check another number (1-yes, 2-no)? ");			int Again1 = console.readInt();			if (Again1 == 1) {				again = true;			} else {				again = false;			}		} while (again = true);	}}